scwin.displayModuleInArea = function (zoneId, moduleId) {
    // ì˜ì—­ ì •ë³´ ê°€ì ¸ì˜¤ê¸°
    var areaComponent = $p.getComponentById(zoneId);
    if (!areaComponent) {
        console.log("ì˜ì—­ì„ ì°¾ì„ ìˆ˜ ì—†ìŠµë‹ˆë‹¤: ", zoneId);
        return;
