// ì„œë¸Œë¯¸ì…˜ ì„±ê³µ ì½œë°± í•¨ìˆ˜
scwin.sbm_moduleList_submitdone = function (e) {
    // ì‘ë‹µ ë°ì´í„°ë¥¼ ì§ì ‘ ê°€ì ¸ì˜¤ê¸°
    if (e && e.responseJSON) {
        //console.log("ì‘ë‹µ ë°ì´í„°:", e.responseJSON);

        // ì§ì ‘ ì‘ë‹µ ë°ì´í„°ë¥¼ ë°›ì•„ì„œ ì²˜ë¦¬... -> ì´ê²Œ ë§ëŠ” ë°©ì‹?!
        scwin.moduleResponseData = e.responseJSON;
