head.script:
scwin.onpageload = function() {
    // 저장된 아이디 로드
    scwin.loadSavedUserId();

    // 엔터키 이벤트 바인딩
    scwin.bindEnterKeyEvents();

    // 커스터마이제이션 값 로드
    scwin.loadCustomization();
    console.log(123123);


};

scwin.loadCustomization = function() {

    var c = $w.data.customization; // 또는
    if (!c) return;

    console.log(c);

    // 배경 이미지 적용
    var bgGroup = $p.getComponentById("leftPreview");
    if (bgGroup && c.backgroundImage) {
        bgGroup.setStyle("backgroundImage", "url(" + c.backgroundImage + ")");
    }

    // 로고
    var logoImg = $p.getComponentById("logoImage");
    if (logoImg && c.logo) {
        logoImg.setSrc(c.logo);
        logoImg.setStyle("width", c.logoSize);
        logoImg.setStyle("height", c.logoSize);
    }

    // 텍스트들
    const setLabelStyle = function(id, text, color, size, weight) {
        var el = $p.getComponentById(id);
        if (!el) return;
        el.setLabel(text);
        el.setStyle("color", color);
        el.setStyle("fontSize", size);
        el.setStyle("fontWeight", weight);
    };

    setLabelStyle("mainTitle", c.mainTitle, c.mainTitleColor, c.mainTitleSize, c.mainTitleFont);
    setLabelStyle("subTitle", c.subTitle, c.subTitleColor, c.subTitleSize, c.subTitleFont);
    setLabelStyle("extraText1", c.extraText1, c.extraText1Color, c.extraText1Size, c.extraText1Font);

    // 오버레이 배경
    var overlay = document.createElement("div");
    overlay.style.position = "absolute";
    overlay.style.top = 0;
    overlay.style.left = 0;
    overlay.style.right = 0;
    overlay.style.bottom = 0;
    overlay.style.backgroundColor = c.overlayColor;
    overlay.style.opacity = (parseInt(c.overlayOpacity) || 0) / 100;
    overlay.style.zIndex = 1;

    var container = $p.getComponentById("leftPreview").getElement();
    if (container) {
        container.appendChild(overlay);
    }
};

// 저장된 아이디 로드
scwin.loadSavedUserId = function() {
    try {
        var savedUserId = localStorage.getItem("savedUserId");
        if (savedUserId) {
            // 데이터 바인딩을 통해 값 설정
            $p.setValueByID("data:loginInfo.userId", savedUserId);
            $p.setValueByID("data:loginInfo.rememberMe", "Y");
        }
    } catch (e) {
        console.warn("로컬 스토리지 접근 실패:", e);
    }
};

// 엔터키 이벤트 바인딩
scwin.bindEnterKeyEvents = function() {
    // 아이디 입력 필드에서 엔터키
    $p.getComponentById("inp_userId").bind("keypress", function(e) {
        if (e.keyCode === 13) {
            e.preventDefault();
            $p.getComponentById("inp_password").focus();
        }
    });

    // 비밀번호 입력 필드에서 엔터키
    $p.getComponentById("inp_password").bind("keypress", function(e) {
        if (e.keyCode === 13) {
            e.preventDefault();
            scwin.login();
        }
    });
};



// 입력값 검증
scwin.validateLogin = function(userId, password) {
    if (!userId || userId.trim() === "") {
        $p.alert("아이디를 입력해주세요.", function() {
            $p.getComponentById("inp_userId").focus();
        });
        return false;
    }

    if (!password || password.trim() === "") {
        $p.alert("비밀번호를 입력해주세요.", function() {
            $p.getComponentById("inp_password").focus();
        });
        return false;
    }
    if (userId.length < 3) {
        $p.alert("아이디는 3글자 이상 입력해주세요.", function() {
            $p.getComponentById("inp_userId").focus();
        });
        return false;
    }

    return true;
};

// 데모용 목업 로그인
scwin.mockLogin = function(userId, password) {
    var validAccounts = [
        { userId: "admin", password: "admin123" },
        { userId: "user", password: "user123" },
        { userId: "test", password: "test123" }
    ];

    setTimeout(function() {
        var isValid = validAccounts.some(function(account) {
            return account.userId === userId && account.password === password;
        });

        if (isValid) {
            scwin.onLoginSuccess();
        } else {
            scwin.onLoginFailure();
        }
    }, 1000);
};

// 로그인 성공 처리
scwin.onLoginSuccess = function() {
    scwin.setLoginButtonState(false);

    var userId = $p.getValueByID("data:loginInfo.userId");
    var rememberMe = $p.getValueByID("data:loginInfo.rememberMe");

    try {
        // 아이디 저장 처리
        if (rememberMe === "Y") {
            localStorage.setItem("savedUserId", userId);
        } else {
            localStorage.removeItem("savedUserId");
        }

        // 사용자 정보 저장
        sessionStorage.setItem("loggedInUser", userId);

    } catch (e) {
        console.warn("스토리지 저장 실패:", e);
    }

    $p.alert("로그인되었습니다.", function() {
        scwin.redirectToMainPage();
    });
};

// 로그인 실패 처리
scwin.onLoginFailure = function() {
    scwin.setLoginButtonState(false);

    $p.alert("아이디 또는 비밀번호가 올바르지 않습니다.", function() {
        $p.setValueByID("data:loginInfo.password", "");
        $p.getComponentById("inp_password").focus();
    });
};

// 로그인 버튼 상태 변경
scwin.setLoginButtonState = function(isLoading) {
    var loginBtn = $p.getComponentById("btn_login");
    if (isLoading) {
        loginBtn.setDisabled(true);
        loginBtn.setText("로그인 중...");
    } else {
        loginBtn.setDisabled(false);
        loginBtn.setText("Login");
    }
};

// 메인 페이지로 리다이렉트
scwin.redirectToMainPage = function() {
    try {
        if (parent && parent.scwin && parent.scwin.goToMain) {
            parent.scwin.goToMain();
        } else {
            console.log("메인 페이지로 이동합니다.");
            // window.location.href = "/main.xml";
        }
    } catch (e) {
        console.log("메인 페이지 이동 처리");
    }
};

// 비밀번호 보기/숨기기 토글
scwin.togglePasswordVisibility = function() {
    var passwordInput = $p.getComponentById("inp_password");
    var toggleBtn = $p.getComponentById("btn_password_toggle");

    if (passwordInput.getInputType() === "password") {
        passwordInput.setInputType("text");
        toggleBtn.setText("비밀번호 숨기기");
    } else {
        passwordInput.setInputType("password");
        toggleBtn.setText("비밀번호 보기");
    }
};

// 실제 서버 통신 완료 후 콜백
scwin.loginSubmitHandler = function(e) {
    if (e.retCode === "0" || e.responseStatusCode === 200) {
        scwin.onLoginSuccess();
    } else {
        scwin.onLoginFailure();
    }
};
